// Output:
//         Hello "World"!
// @"      Hello ""World""!"

string s1 = "\"\\tHello \\\"World\\\"!\"";
string s2 = "@\"\tHello \"\"World\"\"!\"";
Console.WriteLine(s1);
Console.WriteLine(StringUtility.ParseStringLiteral(s1));
Console.WriteLine(s2);
Console.WriteLine(StringUtility.ParseStringLiteral(s2));
// Output:
// "\tHello \"World\"!"
//         Hello "World"!
// @"      Hello ""World""!"
